//engine.js

      class Engine {
  constructor() {
    console.log("Engine loading...")

    this.display = new Display(this)

    console.log("Engine properly initalized")
  }
}      

//bootstrap.js

      let engine = null

function bootstrap() {
  engine = new Engine()
}      

//request.js

      function request(query,callback) {
  const xhttp = new XMLHttpRequest();
  xhttp.onload = callback
  xhttp.open("GET", "query/"+query, true);
  xhttp.send();
}      

//display.js

      class CanvasElement {
  constructor() {
    this.element = document.getElementById("screen")
    this.screen = this.element.getContext("2d")
  }
}


class Display {
  constructor() {
    console.log("Constructing display...")

    console.log("Placing HTML canvas...")
    document.getElementById("body").innerHTML = "<div id='canvas-wrap'><canvas id='screen'>Error loading HTML canvas</canvas></div>"
    console.log("HTML canvas placed")

    this.element = new CanvasElement()
    this.screen = this.element.screen

    this.display_welcome_message()

    console.log("Canvas properly initalized")
  }

  display_welcome_message() {
    var grd = this.screen.createLinearGradient(0, 0, 200, 0);
    grd.addColorStop(0, "red");
    grd.addColorStop(1, "white");

    // Fill with gradient
    this.screen.fillStyle = grd;
    this.screen.fillRect(0, 0, 150, 80);
  }

  getMousePos(canvasDom, mouseEvent) {
    var rect = this.element.getBoundingClientRect();
    return {
      x: (mouseEvent.clientX - rect.left) * this.width/window.innerWidth,
      y: (mouseEvent.clientY - rect.top) * this.width/window.innerWidth
    };
  }
}

css:
body {
  width: 100%;
  height: 100%;
}

html {
  padding: 0px;
  margin: 0px;
  overflow-y: hidden;
}#screen {
  position: fixed;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  background-color: #412f2f;
  display: flex;
  justify-content: center;
  align-items: center;
  height:100%
}

#canvas-wrap {
  object-fit: contain;
  max-height: 100%;
  max-width: 100%;
}.loader {
  height: 4px;
  width: 50%px;
  --c:no-repeat linear-gradient(green 0 0);
  background: var(--c),var(--c),white;
  background-size: 60% 100%;
  animation: l16 3s infinite;
}
@keyframes l16 {
  0%   {background-position:-150% 0,-150% 0}
  66%  {background-position: 250% 0,-150% 0}
  100% {background-position: 250% 0, 250% 0}
}body {
  background-color: black;
}

.icon-tiny {
  width: 50px;
  height: 50px;
}

.title-text {
  color: white;
}

@keyframes fade-in {
  from {opacity: 0;}
  to {opacity: 1;}
}

.title-container {
  animation-name: fade-in;
  animation-duration: 4s;
}
